// src/components/layout/AppNavbar.js
import React from 'react';
import { Navbar, Nav, Container, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

function AppNavbar() {
  const { user } = useAuth();
  return (
    <Navbar bg="dark" variant="dark" expand="lg">
      <Container>
        <Navbar.Brand as={Link} to="/">Inventory System</Navbar.Brand>
        <Nav className="me-auto">
          <Nav.Link as={Link} to="/">Dashboard</Nav.Link>
          <Nav.Link as={Link} to="/products">Products</Nav.Link>
          <Nav.Link as={Link} to="/suppliers">Suppliers</Nav.Link>
          <Nav.Link as={Link} to="/purchase-orders">Purchase Orders</Nav.Link>
        </Nav>
        <Nav>
          <Navbar.Text className="me-3">Signed in as: {user?.username}</Navbar.Text>
          <Button variant="outline-light" onClick={() => { /* This should be logoutUser from context */ }}>Logout</Button>
        </Nav>
      </Container>
    </Navbar>
  );
}

export default AppNavbar;
