import axios from 'axios';

const api = axios.create({
  baseURL: 'http://127.0.0.1:8000/api',
});

export const loginUser = (username, password) => {
  return api.post('/token/', {
    username: username,
    password: password,
  });
};

export const registerUser = (userData) => {
  return api.post('/register/', userData);
};

export const getProducts = () => {
  return api.get('/products/');
};

export const createProduct = (product) => {
  return api.post('/products/', product);
};

export const updateProduct = (id, product) => {
  return api.put(`/products/${id}/`, product);
};

export const deleteProduct = (id) => {
  return api.delete(`/products/${id}/`);
};

// Add these supplier functions
export const getSuppliers = () => {
    return api.get('/suppliers/');
};

export const createSupplier = (supplier) => {
    return api.post('/suppliers/', supplier);
};

export const updateSupplier = (id, supplier) => {
    return api.put(`/suppliers/${id}/`, supplier);
};

export const deleteSupplier = (id) => {
    return api.delete(`/suppliers/${id}/`);
};
export const getPurchaseOrders = () => {
    return api.get('/purchase-orders/');
};

export const createPurchaseOrder = (orderData) => {
    return api.post('/purchase-orders/', orderData);
};


export default api;
