// src/services/api.js
import axios from 'axios';

const api = axios.create({
  baseURL: 'http://127.0.0.1:8000/api', // The base URL of your Django API
});

// Add this function to handle login
export const loginUser = (username, password) => {
  return api.post('/token/', { // This will be the login endpoint
    username: username,
    password: password,
  });
};

export default api;
