// src/pages/ProductsPage.js
import React, { useState, useEffect } from 'react';
import { Container, Table, Spinner, Alert, Button } from 'react-bootstrap';
import TableToolbar from '../components/ui/TableToolbar';
import { useToast } from '../components/ui/ToastProvider';
import { getProducts, createProduct, updateProduct, deleteProduct } from '../services/api';
import ProductFormModal from '../components/ProductFormModal';
import RoleRequired from '../components/RoleRequired';

function ProductsPage() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [search, setSearch] = useState('');
  const { showToast } = useToast();

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await getProducts();
      setProducts(response.data);
      setError('');
    } catch (err) {
      setError('Failed to fetch products.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  const handleShowCreateModal = () => {
    setSelectedProduct(null);
    setShowModal(true);
  };

  const handleShowEditModal = (product) => {
    setSelectedProduct(product);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedProduct(null);
  };

  const handleSave = async (formData) => {
    try {
      if (selectedProduct) {
        await updateProduct(selectedProduct.id, formData);
        showToast({ variant: 'success', message: 'Product updated' });
      } else {
        await createProduct(formData);
        showToast({ variant: 'success', message: 'Product created' });
      }
      fetchProducts();
      handleCloseModal();
    } catch (err) {
      setError(selectedProduct ? 'Failed to update product.' : 'Failed to create product.');
      showToast({ variant: 'danger', message: 'Action failed' });
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure?')) {
      try {
        await deleteProduct(id);
        fetchProducts();
      } catch (err) {
        setError('Failed to delete product.');
      }
    }
  };

  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" />
      </Container>
    );
  }

  return (
    <Container>
      <div className="my-3">
        <div className="page-header d-flex justify-content-between align-items-center">
          <h1 className="mb-0">
            <i className="bi bi-box-seam" /> Products
          </h1>
          <RoleRequired allowedRoles={['Admin', 'Manager']}>
            <Button variant="primary" onClick={handleShowCreateModal}>
              <i className="bi bi-plus-lg me-1" /> Create Product
            </Button>
          </RoleRequired>
        </div>
      </div>

      {error && <Alert variant="danger" className="mb-3">{error}</Alert>}

      <TableToolbar search={search} onSearchChange={setSearch} actions={null} />

      <Table striped bordered hover responsive>
        <thead>
          <tr>
            <th>SKU</th>
            <th>Name</th>
            <th>Stock</th>
            <RoleRequired allowedRoles={['Admin', 'Manager']}>
              <th>Actions</th>
            </RoleRequired>
          </tr>
        </thead>
        <tbody>
          {products
            .filter((p) =>
              [p.sku, p.name].join(' ').toLowerCase().includes(search.toLowerCase())
            )
            .map((product) => (
              <tr key={product.id}>
                <td>{product.sku}</td>
                <td>{product.name}</td>
                <td>{product.stock_quantity}</td>
                <RoleRequired allowedRoles={['Admin', 'Manager']}>
                  <td>
                    <div className="d-flex gap-2">
                      <Button
                        variant="outline-secondary"
                        size="sm"
                        onClick={() => handleShowEditModal(product)}
                      >
                        <i className="bi bi-pencil me-2" /> Edit
                      </Button>
                      <Button
                        variant="outline-danger"
                        size="sm"
                        onClick={() => handleDelete(product.id)}
                      >
                        <i className="bi bi-trash me-1" /> Delete
                      </Button>
                    </div>
                  </td>
                </RoleRequired>
              </tr>
            ))}
        </tbody>
      </Table>

      <ProductFormModal
        show={showModal}
        handleClose={handleCloseModal}
        product={selectedProduct}
        onSave={handleSave}
      />
    </Container>
  );
}

export default ProductsPage;
