// frontend_v2/src/pages/SuppliersPage.js
import React, { useState, useEffect, useCallback } from 'react';
import { Container, Table, Spinner, Alert, Button } from 'react-bootstrap';
import { getSuppliers, createSupplier, updateSupplier, deleteSupplier } from '../services/api';
import SupplierFormModal from '../components/SupplierFormModal';
import TableToolbar from '../components/ui/TableToolbar';
import EmptyState from '../components/ui/EmptyState';
import { useToast } from '../components/ui/ToastProvider';
import SortDropdown from '../components/ui/SortDropdown';

function SuppliersPage() {
  const [suppliers, setSuppliers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [selectedSupplier, setSelectedSupplier] = useState(null);
  const { showToast } = useToast();

  const [search, setSearch] = useState('');
  const [sortOrder, setSortOrder] = useState('name');

  const sortOptions = [
    { value: 'name', label: 'Name (A-Z)' },
    { value: '-name', label: 'Name (Z-A)' },
    { value: '-id', label: 'Most Recent' },
  ];

  const fetchSuppliers = useCallback(async () => {
    try {
      setLoading(true);
      const params = {
        ordering: sortOrder,
        search: search,
      };
      const response = await getSuppliers(params);
      // We now expect a simple array, not a paginated object
      setSuppliers(response.data); 
      setError('');
    } catch (err) {
      setError('Failed to fetch suppliers.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  }, [sortOrder, search]);

  useEffect(() => {
    fetchSuppliers();
  }, [fetchSuppliers]);

  const handleShowCreateModal = () => { setSelectedSupplier(null); setShowModal(true); };
  const handleShowEditModal = (supplier) => { setSelectedSupplier(supplier); setShowModal(true); };
  const handleCloseModal = () => { setShowModal(false); setSelectedSupplier(null); };

  const handleSave = async (formData) => {
    try {
      if (selectedSupplier) {
        await updateSupplier(selectedSupplier.id, formData);
        showToast({ variant: 'success', message: 'Supplier updated' });
      } else {
        await createSupplier(formData);
        showToast({ variant: 'success', message: 'Supplier created' });
      }
      fetchSuppliers();
      handleCloseModal();
    } catch (err) {
      setError(selectedSupplier ? 'Failed to update supplier.' : 'Failed to create supplier.');
      showToast({ variant: 'danger', message: 'Action failed' });
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure? This action cannot be undone.')) {
      try {
        await deleteSupplier(id);
        showToast({ variant: 'success', message: 'Supplier deleted' });
        fetchSuppliers();
      } catch (err) {
        setError('Failed to delete supplier. It might be linked to existing purchase orders.');
        showToast({ variant: 'danger', message: 'Deletion failed' });
      }
    }
  };

  if (loading) return <Container className="text-center mt-5"><Spinner animation="border" /></Container>;

  return (
    <Container>
      <div className="my-3">
        <div className="page-header d-flex justify-content-between align-items-center">
          <h1 className="mb-0"><i className="bi bi-people" /> Suppliers</h1>
          <Button variant="primary" onClick={handleShowCreateModal}><i className="bi bi-plus-lg me-1" /> Create Supplier</Button>
        </div>
      </div>

      <div className="d-flex flex-wrap justify-content-between align-items-center gap-2 mb-3">
        <TableToolbar search={search} onSearchChange={setSearch} />
        <SortDropdown options={sortOptions} value={sortOrder} onChange={setSortOrder} />
      </div>

      {error && <Alert variant="danger" className="mb-3">{error}</Alert>}

      {!loading && suppliers.length === 0 ? (
        <EmptyState title="No suppliers found" description="Try adjusting your search or create a new supplier." action={<Button variant="primary" onClick={handleShowCreateModal}>Create Supplier</Button>} />
      ) : (
        <Table striped hover responsive>
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
              <th>Phone</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {suppliers.map((supplier) => (
              <tr key={supplier.id}>
                <td>{supplier.name}</td>
                <td>{supplier.email}</td>
                <td>{supplier.phone}</td>
                <td>
                  <div className="d-flex gap-2">
                    <Button variant="outline-secondary" size="sm" onClick={() => handleShowEditModal(supplier)}><i className="bi bi-pencil me-1" /> Edit</Button>
                    <Button variant="outline-danger" size="sm" onClick={() => handleDelete(supplier.id)}><i className="bi bi-trash me-1" /> Delete</Button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
      <SupplierFormModal show={showModal} handleClose={handleCloseModal} supplier={selectedSupplier} onSave={handleSave} />
    </Container>
  );
}

export default SuppliersPage;
