// frontend_v2/src/pages/AuditTrailPage.js
import React, { useState, useEffect, useCallback, useMemo } from 'react';
import {
  Box,
  Card,
  CardContent,
  CircularProgress,
  Alert,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  Chip,
  Paper,
  TablePagination,
  TextField,
  TableSortLabel,
} from '@mui/material';
import { getAuditLogs } from '../services/api';
import EmptyState from '../components/ui/EmptyState';
import PersonIcon from '@mui/icons-material/Person';

function AuditTrailPage() {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [orderBy, setOrderBy] = useState('timestamp');
  const [order, setOrder] = useState('desc');
  const [search, setSearch] = useState('');

  const fetchLogs = useCallback(async () => {
    try {
      setLoading(true);
      const response = await getAuditLogs();
      setLogs(response.data);
    } catch (err) {
      setError(
        'Failed to fetch audit logs. You may not have permission to view this page.'
      );
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchLogs();
  }, [fetchLogs]);

  const handleSort = (field) => {
    const isAsc = orderBy === field && order === 'asc';
    setOrder(isAsc ? 'desc' : 'asc');
    setOrderBy(field);
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const filteredLogs = useMemo(() => {
    return logs.filter(
      (log) =>
        log.user?.username?.toLowerCase().includes(search.toLowerCase()) ||
        log.action.toLowerCase().includes(search.toLowerCase()) ||
        log.object_repr.toLowerCase().includes(search.toLowerCase())
    );
  }, [logs, search]);

  const sortedLogs = useMemo(() => {
    return [...filteredLogs].sort((a, b) => {
      let aValue = a[orderBy];
      let bValue = b[orderBy];

      if (orderBy === 'timestamp') {
        aValue = new Date(aValue);
        bValue = new Date(bValue);
      } else if (orderBy === 'user') {
        aValue = a.user?.username || '';
        bValue = b.user?.username || '';
      }

      if (aValue < bValue) return order === 'asc' ? -1 : 1;
      if (aValue > bValue) return order === 'asc' ? 1 : -1;
      return 0;
    });
  }, [filteredLogs, order, orderBy]);

  const paginatedLogs = useMemo(() => {
    return sortedLogs.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage);
  }, [sortedLogs, page, rowsPerPage]);

  if (loading)
    return (
      <Box display="flex" justifyContent="center" mt={5}>
        <CircularProgress />
      </Box>
    );

  return (
    <Box sx={{ my: 4, px: 2 }}>
      {/* Page Header */}
      <Box mb={3}>
        <Typography
          variant="h4"
          component="h2"
          fontWeight="600"
          display="flex"
          alignItems="center"
          mb={1}
        >
          <PersonIcon color="primary" sx={{ mr: 1 }} /> Audit Trail
        </Typography>
        <Typography variant="body2" color="text.secondary">
          A log of all significant user actions in the system.
        </Typography>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      <Box mb={2} maxWidth={300}>
        <TextField
          fullWidth
          size="small"
          label="Search logs"
          variant="outlined"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </Box>

      {!loading && paginatedLogs.length === 0 ? (
        <EmptyState
          title="No audit logs found"
          description="There are no records available to display at the moment."
        />
      ) : (
        <Card elevation={3}>
          <CardContent sx={{ p: 0 }}>
            <TableContainer component={Paper} sx={{ maxHeight: 600 }}>
              <Table stickyHeader aria-label="audit logs table">
                <TableHead>
                  <TableRow>
                    <TableCell sortDirection={orderBy === 'timestamp' ? order : false}>
                      <TableSortLabel
                        active={orderBy === 'timestamp'}
                        direction={orderBy === 'timestamp' ? order : 'asc'}
                        onClick={() => handleSort('timestamp')}
                      >
                        Timestamp
                      </TableSortLabel>
                    </TableCell>
                    <TableCell sortDirection={orderBy === 'user' ? order : false}>
                      <TableSortLabel
                        active={orderBy === 'user'}
                        direction={orderBy === 'user' ? order : 'asc'}
                        onClick={() => handleSort('user')}
                      >
                        User
                      </TableSortLabel>
                    </TableCell>
                    <TableCell>Action</TableCell>
                    <TableCell>Object</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {paginatedLogs.map((log) => (
                    <TableRow key={log.id} hover>
                      <TableCell>
                        {new Date(log.timestamp).toLocaleString()}
                      </TableCell>
                      <TableCell>{log.user ? log.user.username : 'System'}</TableCell>
                      <TableCell>
                        <Chip
                          label={log.action}
                          color={
                            log.action === 'CREATED'
                              ? 'success'
                              : log.action === 'DELETED'
                              ? 'error'
                              : 'default'
                          }
                          size="small"
                        />
                      </TableCell>
                      <TableCell>{log.object_repr}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            <TablePagination
              component="div"
              count={sortedLogs.length}
              page={page}
              onPageChange={handleChangePage}
              rowsPerPage={rowsPerPage}
              onRowsPerPageChange={handleChangeRowsPerPage}
              rowsPerPageOptions={[5, 10, 25, 50]}
            />
          </CardContent>
        </Card>
      )}
    </Box>
  );
}

export default AuditTrailPage;
