// frontend_v2/src/pages/AuditTrailPage.js
import React, { useState, useEffect, useCallback } from 'react';
import {
  Box,
  Card,
  CardContent,
  CircularProgress,
  Alert,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Typography,
  Chip,
  Paper,
} from '@mui/material';
import { getAuditLogs } from '../services/api';
import EmptyState from '../components/ui/EmptyState';
import PersonIcon from '@mui/icons-material/Person';

function AuditTrailPage() {
  const [logs, setLogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchLogs = useCallback(async () => {
    try {
      setLoading(true);
      const response = await getAuditLogs();
      setLogs(response.data);
    } catch (err) {
      setError(
        'Failed to fetch audit logs. You may not have permission to view this page.'
      );
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchLogs();
  }, [fetchLogs]);

  if (loading)
    return (
      <Box display="flex" justifyContent="center" mt={5}>
        <CircularProgress />
      </Box>
    );

  return (
    <Box sx={{ my: 4, px: 2 }}>
      {/* Page Header */}
      <Box mb={3}>
        <Typography variant="h4" component="h2" fontWeight="600" display="flex" alignItems="center" mb={1}>
          <PersonIcon color="primary" sx={{ mr: 1 }} /> Audit Trail
        </Typography>
        <Typography variant="body2" color="text.secondary">
          A log of all significant user actions in the system.
        </Typography>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {!loading && logs.length === 0 ? (
        <EmptyState
          title="No audit logs found"
          description="There are no records available to display at the moment."
        />
      ) : (
        <Card elevation={3}>
          <CardContent sx={{ p: 0 }}>
            <TableContainer component={Paper} sx={{ maxHeight: 600 }}>
              <Table stickyHeader aria-label="audit logs table">
                <TableHead>
                  <TableRow>
                    <TableCell>Timestamp</TableCell>
                    <TableCell>User</TableCell>
                    <TableCell>Action</TableCell>
                    <TableCell>Object</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {logs.map((log) => (
                    <TableRow key={log.id} hover>
                      <TableCell>
                        {new Date(log.timestamp).toLocaleString()}
                      </TableCell>
                      <TableCell>{log.user ? log.user.username : 'System'}</TableCell>
                      <TableCell>
                        <Chip
                          label={log.action}
                          color={
                            log.action === 'CREATED'
                              ? 'success'
                              : log.action === 'DELETED'
                              ? 'error'
                              : 'default'
                          }
                          size="small"
                        />
                      </TableCell>
                      <TableCell>{log.object_repr}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </CardContent>
        </Card>
      )}
    </Box>
  );
}

export default AuditTrailPage;
