// frontend_v2/src/pages/ProductsPage.js

import React, { useState, useEffect, useCallback } from 'react';
import { Container, Table, Spinner, Alert, Button, Card } from 'react-bootstrap';
import TableToolbar from '../components/ui/TableToolbar';
import { useToast } from '../components/ui/ToastProvider';
import { getProducts, createProduct, updateProduct, patchProduct } from '../services/api';
import ProductFormModal from '../components/ProductFormModal';
import RoleRequired from '../components/RoleRequired';
import SortDropdown from '../components/ui/SortDropdown';
import EmptyState from '../components/ui/EmptyState';

function ProductsPage() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const { showToast } = useToast();

  const [search, setSearch] = useState('');
  const [sortOrder, setSortOrder] = useState('-id');

  const sortOptions = [
    { value: '-id', label: 'Most Recent' },
    { value: 'name', label: 'Name (A-Z)' },
    { value: '-name', label: 'Name (Z-A)' },
    { value: '-stock_quantity', label: 'Stock (High-Low)' },
    { value: 'stock_quantity', label: 'Stock (Low-High)' },
  ];

  const fetchProducts = useCallback(async () => {
    try {
      setLoading(true);
      const params = {
        ordering: sortOrder,
        search: search,
      };
      const response = await getProducts(params);
      setProducts(response.data); // Expect a simple array now
      setError('');
    } catch (err) {
      setError('Failed to fetch products.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  }, [sortOrder, search]);

  useEffect(() => {
    fetchProducts();
  }, [fetchProducts]);

  const handleShowCreateModal = () => {
    setSelectedProduct(null);
    setShowModal(true);
  };

  const handleShowEditModal = (product) => {
    setSelectedProduct(product);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setSelectedProduct(null);
  };

  const handleSave = async (formData) => {
    try {
      if (selectedProduct) {
        await updateProduct(selectedProduct.id, formData);
        showToast({ variant: 'success', message: 'Product updated' });
      } else {
        await createProduct(formData);
        showToast({ variant: 'success', message: 'Product created' });
      }
      fetchProducts();
      handleCloseModal();
    } catch (err) {
      setError(selectedProduct ? 'Failed to update product.' : 'Failed to create product.');
      showToast({ variant: 'danger', message: 'Action failed' });
    }
  };

  const handleArchive = async (id) => {
    if (window.confirm('Are you sure you want to archive this product? It will be hidden from lists and new orders.')) {
      try {
        await patchProduct(id, { is_active: false });
        showToast({ variant: 'success', message: 'Product archived successfully' });
        fetchProducts();
      } catch (err) {
        const errorMessage = err.response?.data?.detail || 'Failed to archive product.';
        setError(errorMessage);
        showToast({ variant: 'danger', message: 'Action failed' });
      }
    }
  };

  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" />
      </Container>
    );
  }

  return (
    <Container className="my-4">
      <Card className="shadow-sm border-0">
        <Card.Header className="d-flex justify-content-between align-items-center bg-light">
          <h3 className="mb-0">
            <i className="bi bi-box-seam me-2" /> Products
          </h3>
          <div className="d-flex gap-3 align-items-center">
            <SortDropdown options={sortOptions} value={sortOrder} onChange={setSortOrder} />
            <TableToolbar search={search} onSearchChange={setSearch} actions={null} />
            <RoleRequired allowedRoles={['Admin', 'Manager']}>
              <Button variant="primary" onClick={handleShowCreateModal}>
                <i className="bi bi-plus-lg me-1" /> Create
              </Button>
            </RoleRequired>
          </div>
        </Card.Header>

        <Card.Body>
          {error && <Alert variant="danger" className="mb-3">{error}</Alert>}

          {!loading && products.length === 0 ? (
            <EmptyState title="No products found" description="Try adjusting your search or create a new product." action={
              <RoleRequired allowedRoles={['Admin', 'Manager']}>
                <Button variant="primary" onClick={handleShowCreateModal}>Create Product</Button>
              </RoleRequired>
            } />
          ) : (
            <Table hover responsive borderless className="align-middle">
              <thead className="table-light">
                <tr>
                  <th>SKU</th>
                  <th>Name</th>
                  <th>Stock</th>
                  <RoleRequired allowedRoles={['Admin', 'Manager']}>
                    <th className="text-center">Actions</th>
                  </RoleRequired>
                </tr>
              </thead>
              <tbody>
                {products
                  .filter((p) =>
                    [p.sku, p.name].join(' ').toLowerCase().includes(search.toLowerCase())
                  )
                  .map((product) => (
                    <tr key={product.id}>
                      <td>{product.sku}</td>
                      <td>{product.name}</td>
                      <td>{product.stock_quantity}</td>
                      <RoleRequired allowedRoles={['Admin', 'Manager']}>
                        <td className="text-center">
                          <div className="d-flex justify-content-center gap-2">
                            <Button
                              variant="outline-secondary"
                              size="sm"
                              onClick={() => handleShowEditModal(product)}
                            >
                              <i className="bi bi-pencil" /> Edit
                            </Button>
                            <Button
                              variant="outline-danger"
                              size="sm"
                              onClick={() => handleArchive(product.id)}
                            >
                              <i className="bi bi-archive" /> Archive
                            </Button>
                          </div>
                        </td>
                      </RoleRequired>
                    </tr>
                  ))}
              </tbody>
            </Table>
          )}
        </Card.Body>
      </Card>

      <ProductFormModal
        show={showModal}
        handleClose={handleCloseModal}
        product={selectedProduct}
        onSave={handleSave}
      />
    </Container>
  );
}

export default ProductsPage;
